@classmethod
def setUpClass(cls):
    cls.ev_loop = asyncio.get_event_loop()
    if API_MOCK_ENABLED:
        cls.web_app = HummingWebApp.get_instance()
        cls.web_app.add_host_to_mock(API_HOST, ["/products", "/currencies"])
        cls.web_app.start()
        cls.ev_loop.run_until_complete(cls.web_app.wait_til_started())

